@@ -0,0 +1,651 @@
+/*=============== GOOGLE FONTS ===============*/
+
+@import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@100;200;300;400;500;600;700;800;900&display=swap");
+
+/*=============== VARIABLES ===============*/
+
+:root {
+  --accent-color: #a8858f;
+  --background-color: #302729;
+  --background-color-2: #362b2e;
+  --light-grey: rgb(194, 194, 194);
+  --border-radius: 30px;
+}
+
+/*=============== SCROLLBAR ===============*/
+
+::-webkit-scrollbar {
+  width: 8px;
+}
+
+::-webkit-scrollbar-track {
+  background: transparent;
+}
+
+::-webkit-scrollbar-thumb {
+  background: transparent;
+  border-radius: 10px;
+}
+
+.content:hover::-webkit-scrollbar-thumb {
+  background: var(--light-grey);
+}
+
+/*=============== BASE ===============*/
+
+* {
+  padding: 0;
+  margin: 0;
+  font-family: "Montserrat", sans-serif;
+}
+
+html {
+  font-size: 16px;
+  font-family: "Montserrat", sans-serif;
+  background-color: var(--background-color);
+}
+
+a {
+  text-decoration: none;
+  color: var(--accent-color);
+}
+
+p {
+  line-height: 1.5rem;
+  margin-bottom: 0.7rem;
+}
+
+h1 {
+  line-height: 4rem;
+}
+
+h2 {
+  line-height: 2rem;
+  font-weight: normal;
+}
+
+.col-2 {
+  display: grid;
+  grid-template-columns: 1fr 1fr;
+  gap: 20px;
+  width: 100%;
+}
+
+.container {
+  display: flex;
+  align-items: center;
+  justify-content: center;
+  height: 100vh;
+}
+
+/*=============== MENU ===============*/
+
+.menu {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  order: 2;
+  background: #fff;
+  width: 70px;
+  margin-left: 20px;
+  border-radius: 35px;
+  padding: 15px 0;
+  box-shadow: 0px 0px 20px 1px rgba(0, 0, 0, 0.1);
+}
+
+.menu-icon {
+  font-size: 2rem;
+  color: var(--light-grey);
+  margin: 15px;
+}
+
+.menu-icon:hover {
+  color: var(--accent-color);
+}
+
+/*=============== PORTFOLIO CONTAINER ===============*/
+
+.portfolio {
+  display: flex;
+  background: var(--background-color-2);
+  height: 80vh;
+  width: 85vw;
+  max-width: 1200px;
+  border-radius: var(--border-radius);
+  box-shadow: 0px 0px 20px 1px rgba(0, 0, 0, 0.1);
+}
+
+/*=============== HEADER ===============*/
+
+.header {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  width: 500px;
+  height: 100%;
+  border-radius: var(--border-radius);
+  color: white;
+  overflow: auto;
+}
+
+.header-img {
+  width: 150px; /* Lebar gambar 150px */
+  height: auto; /* Tinggi gambar disamakan dengan lebar */
+  border-radius: 50%; /* Membentuk gambar menjadi lingkaran */
+  margin: 10px;
+  margin-top: 100px;
+  box-shadow: 0px 0px 10px 2px rgba(0, 0, 0, 0.5);
+  object-fit: cover;       /* Memastikan gambar mengisi area lingkaran tanpa distorsi */
+}
+
+.header h1 {
+  font-size: 2.5rem;
+  text: center;
+}
+
+.header h2 {
+  font-size: 1rem;
+  text-align: center;
+}
+
+.socials {
+  margin: 20px;
+}
+
+.socials a {
+  color: white;
+  margin: 0 10px;
+  transition: 0.2s linear;
+  transition-property: background-color, color;
+}
+
+.socials a:hover {
+  color: var(--accent-color);
+  transition: 0.2s linear;
+  transition-property: background-color, color;
+}
+
+.header .cta {
+  color: #fff;
+  background-color: rgba(255, 255, 255, 0.274);
+  padding: 10px 20px;
+  margin: 50px 0;
+  border-radius: 30px;
+  transition: 0.2s linear;
+  transition-property: background-color, color;
