+  background: white;
+  width: 100%;
+  height: 100%;
+  overflow: auto;
+  scroll-behavior: smooth;
+}
+
+.content-card {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  justify-content: center;
+  min-height: 100%;
+  margin: 0 50px;
+}
+
+/*=============== HOME SECTION ===============*/
+
+.home h1 {
+  font-size: 3.5rem;
+  font-weight: bolder;
+  text-align: center;
+  margin: 20px;
+}
+
+/*=============== ABOUT SECTION ===============*/
+
+.about {
+  display: flex;
+  flex-direction: column;
+}
+
+.about-item {
+  width: 100%;
+  margin: 0.5rem 0;
+}
+
