+/* Styling untuk modal */
+
+/* Modal */
+.modal {
+  display: none;
+  position: fixed;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  background-color: rgba(0, 0, 0, 0.8); /* Latar belakang semi-transparan untuk modal */
+  justify-content: center;
+  align-items: center;
+  z-index: 1000;
+}
+
+/* Konten modal */
+.modal-content {
+  position: relative;
+  background-color: #fff;
