+/*=============== GOOGLE FONTS ===============*/
+
+@import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@100;200;300;400;500;600;700;800;900&display=swap");
+
+/*=============== VARIABLES ===============*/
+
+:root {
+  --accent-color: #a8858f;
+  --background-color: #302729;
+  --background-color-2: #362b2e;
+  --light-grey: rgb(194, 194, 194);
+  --border-radius: 30px;
+}
+
+/*=============== SCROLLBAR ===============*/
