+}
+
+.languages h1 {
+  font-size: 24px;
+  color: #333;
+  margin-bottom: 20px;
+}
+
+/* Language progress bar container */
+.language {
+  margin-bottom: 15px;
+}
+
+.language p {
+  font-size: 16px;
+  color: #333;
+  margin-bottom: 5px;
+}
+
+/* Progress bar container */
+.bar {
