+
+
+/*=============== PROJECTS SECTION ===============*/
+
+.project-list {
+  margin: 20px 0;
+}
+
+.project-tile {
+  position: relative;
+  display: flex;
+  flex-direction: column;
+  justify-content: end;
+  background: var(--light-grey);
+  color: transparent;
+  width: 100%;
+  height: 300px;
+  margin: auto;
+  overflow: hidden;
+  box-shadow: 0px 0px 10px 1px rgba(0, 0, 0, 0.1);
+}
+
+.project-tile img {
+  position: absolute;
+  width: 100%;
+  height: 100%;
+  object-fit: cover;
+  z-index: 1;
+  filter: contrast(70%) brightness(110%) grayscale(20%);
+}
+
+.overlay {
+  background: #583C4487;
+  display: flex;
+  flex-direction: column;
+  justify-content: end;
