+  align-items: center;
+  z-index: 1000;
+}
+
+/* Konten modal */
+.modal-content {
+  position: relative;
+  background-color: #fff;
+  padding: 20px;
+  width: 80%;
+  max-width: 600px;
+  text-align: center;
+  border-radius: 8px;
+}
+
+.close-btn, .prev-btn, .next-btn {
+  font-size: 24px;
+  color: #333;
+  cursor: pointer;
+  position: absolute;
+  top: 50%;
+  transform: translateY(-50%);
+}
+
+.close-btn {
+  top: 10px;
+  right: 20px;
+  font-size: 30px;
+  color: #fff;
+}
+
+.prev-btn {
+  left: 10px;
+}
+
+.next-btn {
+  right: 10px;
+}
+
+/* Gambar modal */
