+}
+
+
+/*=============== FORM ===============*/
+
+form {
+  width: 100%;
+  max-width: 600px;
+}
+
+.input-box {
+  display: flex;
+  align-items: center;
+}
+
+.input-box input,
+textarea {
+  width: 100%;
+  margin: 1rem;
