+    background-color: white; /* Tambahkan warna latar belakang jika diperlukan */
+    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2); /* Tambahkan bayangan untuk efek kedalaman */
+  }
+
+  .menu-icon {
+    font-size: 24px; /* Ukuran ikon */
