+
+  ::-webkit-scrollbar {
+    display: none;
+  }
+
+  .content-card {
+    height: auto; /* mengatur tinggi secara otomatis */
+    max-height: 300px;
+  }
+}
+
+@media screen and (max-width: 400px) {
+  .content-card {
+    margin: 0 30px;
